SELECT 
  hub_departer, 
  COUNT(*) as segment_count,
  COUNT(DISTINCT route_name) as route_count,
  COUNT(DISTINCT hub_destination) as destination_count
FROM route_schedules
GROUP BY hub_departer
ORDER BY segment_count DESC;

